package eduJavaCode;

public class ArithematicOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int number1 = 12, number2 = 3, result;
		
		result = number1 + number2;
		System.out.println("number1 + number2 = " +result);
		
		result = number1 - number2;
		System.out.println("number1 - number2 = " +result);
		
		result = number1 * number2;
		System.out.println("number1 * number2 = " +result);
		
		result = number1 / number2;
		System.out.println("number1 / number2 = " +result);
		
		result = number1 % number2;
		System.out.println("number1 % number2 = " +result);
		
		
		
		
	}

}
