package eduJavaCode;

public class EvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 25;
		
		if (a%2 == 0)
		{
			System.out.println("Number is even number");
		}
		else
		{
			System.out.println("Number is odd number");
		}
		
	}

}
