package eduJavaCode;

public class UnaryOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a, b = 5;
		
		a = +b;
		
		System.out.println(a);
		
		a = -b;
		
		System.out.println(a);
		
		a = ++b ; //b = b + 1
		
		System.out.println(a);
		
		a = --b;  //b = b - 1
		System.out.println(a);
		
	}

}
