package eduJavaCode;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		int sum = 0, i = 0;
		
		while(i != 0)
		{
			sum += i; 	//sum = sum + i     394
			--i;		//i = i - 1			96
		}
		
		System.out.println(sum);
		
	}

}
